// ==UserScript==
// @name        Hidden Elements Viewer
// @namespace   hev_
// @description Show and hide some hidden html elements
// @version     0.1a
// @require  http://ajax.googleapis.com/ajax/libs/jquery/1.7.2/jquery.min.js
// ==/UserScript==

// Prevent the script to run in iframes
if (window.top != window.self) return;

var hev_target = $('body *');
var hev_showScripts = false;

hev_injectCSS();
hev_appendSidebar();
fix_hev_Sidebar_Height();
hev_attach_handlers();
hev_refresh();

/* CSS that will be used by the sidebar */
function hev_injectCSS()
{
	var newCSS =
	'<style id="HiddenElementsViewer" type="text/css">\
	#hev_sidebar_container * { font: 1 Arial,sans-serif !important; }\
	#hev_sidebar_container\
	{\
		color: #000;\
		width: 300px;\
		float:left;\
		position: fixed;\
		left: 0;\
		top: 0;\
		border-right: 1px solid;\
		overflow: hidden;\
		-webkit-box-shadow: 5px 0px 5px -3px;\
		-moz-box-shadow: 5px 0px 5px -3px;\
		box-shadow: 5px 0px 5px -3px;\
		z-index: 50000;\
		opacity: 0.9;\
		-webkit-opacity: 0.9;\
		-moz-opacity: 0.9;\
		background: #fff url(data:image/png;base64,) repeat;\
	}\
	#hev_sidebar_container h1\
	{\
		text-align: center;\
		size: 18px;\
		color: black;\
		margin: 10px 0;\
		padding: 0;\
	}\
	#hev_sidebar_content {\
		overflow-y: scroll;\
		width: 322px;\
	}\
	#hev_logo {\
		background: url(data:image/png;base64,) no-repeat;\
		width: 258px;\
		height: 95px;\
		margin: 0 auto;\
	}\
	#hev_sidebar_container a:visited {\
		color: #000;\
	}\
	#hev_list {\
		list-style: none;\
		margin:0;\
		padding:0;\
		text-align: left;\
	}\
	#hev_list > li {\
		border-top: 1px solid #fff;\
		border-bottom: 1px solid #aaa;\
		padding: 5px;\
	}\
	#hev_list > li:first-child {\
		border-top: none;\
	}\
	#hev_list > li:last-child {\
		border-bottom: none;\
	}\
	.hev_btn {\
		-moz-transition: all 0.5s ease-in-out 0s;\
		background-repeat: repeat-x;\
		border-bottom: 1px solid rgba(0, 0, 0, 0.25);\
		border-radius: 10px 10px 10px 10px;\
		box-shadow: 0 1px 4px rgba(0, 0, 0, 0.6);\
		display: inline-block;\
		margin: 3px;\
		outline: 0 none;\
		position: relative;\
		text-decoration: none;\
		text-shadow: 0 1px 2px #FFFFFF;\
		font-size: 14px;\
		line-height: 1em;\
		padding: 9px 14px;\
	}\
	.hev_btn:hover {\
		box-shadow: 0 1px 5px #939393;\
		opacity: 0.8;\
		-moz-opacity: 0.8;\
		-webkit-opacity: 0.8;\
		text-shadow: 0 1px 2px #FFFFFF;\
	}\
	.hev_toggle {\
		background: url(data:image/png;base64,) no-repeat;\
		display: block;\
		float: right;\
		height: 16px;\
		width: 32px;\
	}\
	.hev_toggle_hide {\
		background-position: bottom center;\
	}\
	.hev_toggle_hide:hover {\
		background-position: top center;\
	}\
	.hev_toggle_show {\
 		background-position: top center;\
	}\
	.hev_toggle_show:hover {\
		background-position: bottom center;\
	}\
	#hev_toggle_sidebar {\
		background: url(data:image/png;base64,) no-repeat;\
	    background-position: right center;\
		width: 16px;\
		height: 32px;\
	    left: 293px;\
	    position: fixed;\
	    top: 10px;\
		z-index: 50001;\
	}\
	#hev_toggle_sidebar:hover {\
		background-position: left center;\
	}\
	#hev_counter {\
		top: 10px;\
		margin-left: 315px;\
		margin-top: 18px;\
		z-index: 60000;\
		box-shadow: 0 0 3px 1px #000000;\
		-moz-box-shadow: 0 0 3px 1px #000000;\
		-webkit-box-shadow: 0 0 3px 1px #000000;\
		position: fixed;\
	}\
	a#hev_counter:visited {\
		color: #fff\
	}\
	.hev_badge {\
		background-color: #999999;\
		border: 1px solid #AAAAAA;\
		border-radius: 9px 9px 9px 9px;\
		-moz-border-radius: 9px 9px 9px 9px;\
		-webkit-border-radius: 9px 9px 9px 9px;\
		color: #FFFFFF;\
		display: block;\
		font-size: 10.998px;\
		font-weight: bold;\
		line-height: 14px;\
		padding: 1px 9px 2px;\
		text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);\
		vertical-align: baseline;\
		white-space: nowrap;\
	}\
	.hev_red {\
		background-color: #B94A48 !important;\
	}\
	.hev_hidden_unhide {\
		width: auto !important;\
		height: auto !important;\
	}\
	.hev_opacity_unhide {\
		opacity: 1 !important;\
	}\
	.hev_display_unhide {\
		display: block !important;\
	}\
	.hev_visibility_unhide {\
		visibility: visible !important;\
	}\
	.hev_text-indent_unhide {\
		text-indent: 0px !important;\
	}\
	</style>';

	/* Let's add the css in the head tag ;) */
	$('head').append(newCSS);
}

/* Adds the sidebar layout in the body of the page */
function hev_appendSidebar()
{
	var sidebarLayout =
	'<div id="hev_sidebar_container">\
		<div id="hev_sidebar_content">\
			<div id="hev_logo"></div>\
			<div align="center">\
				<a href="#" id="hev_refresh" class="hev_btn">Refresh</a>\
				<a href="#" id="hev_show_all" class="hev_btn">Show all</a>\
				<a href="#" id="hev_hide_all" class="hev_btn">Hide all</a>\
			</div>\
			<ul id="hev_list">\
			</ul>\
		</div>\
	</div>\
	<a href="#" id="hev_toggle_sidebar"></a>\
	<a href="#" id="hev_counter" class="hev_badge"></a>\
	';

	$('body').prepend(sidebarLayout);
}

/* Actions that will be written  */
function hev_attach_handlers()
{
	var sidebar = $('#hev_sidebar_container');
	var sidebarlength = sidebar.outerWidth();

	$("#hev_toggle_sidebar").on("click", function() {
	    // Prevent action while animating or when sidebar is hidden
	    if (sidebar.is(":animated")) {
	    	return false;
	    }
	    else {
	    	if (sidebar.is(":hidden"))
	    	{
	    		$(this).css({
	    			"background-position": "right"
	    		});
		    	$(this).animate({
		    		"left": "+=" + sidebarlength + "px"
		    	}, "slow");
		    	$("#hev_counter").animate({
		    		"left": "+=" + sidebarlength + "px"
		    	}, "slow");
		    	sidebar.animate({
		    		"margin-left": "+=" + sidebarlength + "px",
		    		opacity: "show"
		    	}, "slow");
		    }
		    else
		    {
		    	$(this).css({
	    			"background-position": "left"
	    		});
		    	$(this).animate({
		    		"left": "-=" + sidebarlength + "px"
		    	}, "slow");
		    	$("#hev_counter").animate({
		    		"left": "-=" + sidebarlength + "px"
		    	}, "slow");
		    	sidebar.animate({
		    		"margin-left": "-=" + sidebarlength + "px",
		    		opacity: "hide"
		    	}, "slow");
		    }
	    }
	});

	$("#hev_refresh").on("click", function(e)
	{
		e.preventDefault(e);
		hev_refresh();
	});

	$("#hev_counter").on("click", function(e)
	{
		e.preventDefault(e);
		hev_refresh();
	});

	$(".hev_hide").on("click", function(e)
	{
		e.preventDefault(e);
		hev_hide($(this).location.hash.substr(1));
		hev_refresh();
	});

	$(".hev_show").on("click", function(e)
	{
		e.preventDefault(e);
		hev_show($(this).location.hash.substr(1));
		hev_refresh();
	});

	$("#hev_show_all").on("click", function(e)
	{
		e.preventDefault(e);
		hev_show_all();
	});

	$("#hev_hide_all").on("click", function(e)
	{
		e.preventDefault(e);
		hev_hide_all();
	});
}

function hev_refresh()
{
	
	var hiddenType = "";
	var hev_unique_id = "0";
	/* Counter */
	var hev_counter = hev_target.filter(function()
	{
		// Do we care about scripts?
		if (!hev_showScripts)
			if($(this).is('script'))
				return false;

		//hidden0
		if ($(this).is(':hidden')) 
			$(this).addClass('hev_hidden');
		//hidden1
		if ($(this).css('visibility') === 'hidden') 
			$(this).addClass('hev_visibility');
		//hidden2
		if ($(this).css('display') === 'none') 
			$(this).addClass('hev_display');
		//hidden3
		if ($(this).css('opacity') === '0') 
			$(this).addClass('hev_opacity');
		//hidden4
		if ($(this).css('text-indent') < '0')
			$(this).addClass('hev_text-indent');

		if ($(this).hasClass('hev_hidden') || $(this).hasClass('hev_visibility') || $(this).hasClass('hev_display') || $(this).hasClass('hev_opacity') || $(this).hasClass('hev_text-indent'))
		{
			$(this).removeData();
			$(this).data("hev_unique_id", hev_unique_id);
			hev_unique_id++;
			return true;
		}
		else
		{
			return false;
		}
	})
	if (hev_counter.size() > 0)
	{
		$("#hev_counter").addClass("hev_red");
	}
	else {
		$("#hev_counter").removeClass("hev_red");
	}
	$("#hev_counter").html(hev_counter.size());

	/* List of elements */
	var result = "";
	hev_counter.each( function(index){
		
		var hev_classes = $(this).attr("class").split(' ');
		var hev_original_classes = [''];

		result +=	"<li>" + index + ": &lt;" + this.tagName.toLowerCase() + " ";
		
		if ($(this).attr("id") != null)
			result += "id='" + $(this).attr("id") + "' ";

		if (hev_classes != null)
		{
			var i = 0, j = 0;
			for (i; i < hev_classes.length; i++)
			{
				if (hev_classes[i] != 'hev_hidden' &&						
					hev_classes[i] != 'hev_hidden_unhide' &&
					hev_classes[i] != 'hev_visibility' &&						
					hev_classes[i] != 'hev_visibility_unhide' &&
					hev_classes[i] != 'hev_display' &&						
					hev_classes[i] != 'hev_display_unhide' &&
					hev_classes[i] != 'hev_opacity' &&
					hev_classes[i] != 'hev_opacity_unhide' &&
					hev_classes[i] != 'hev_text-indent' &&
					hev_classes[i] != 'hev_text-indent_unhide')
				{
					hev_original_classes[j] = hev_classes[i];
					j++;
				}
			}
			if (j > 0)
			{
				result += "class='";
				result += hev_original_classes;
				result += "'";
			}
		}
		
		result += 	"&gt";
		
		/* if objets are already visible */
		if($(this).is('.hev_hidden_unhide') || $(this).is('.hev_visibility_unhide')  || $(this).is('.hev_opacity_unhide') || $(this).is('.hev_display_unhide'))
			result += 	"<a href='#" + $(this).data('hev_unique_id') + "' class='hev_toggle hev_toggle_hide'></a>";
		else
			result += 	"<a href='#" + $(this).data('hev_unique_id') + "' class='hev_toggle hev_toggle_show'></a>";
		result +=	"</li>";
	});
	$("#hev_list").html(result);
}

// Show all elements
// Triggered by the show all button
function hev_show_all()
{
	$('.hev_hidden').addClass("hev_hidden_unhide");
	$('.hev_visibility').addClass("hev_visibility_unhide");
	$('.hev_display').addClass("hev_display_unhide");
	$('.hev_opacity').addClass("hev_opacity_unhide");
	$('.hev_text-indent').addClass("hev_text-indent_unhide");
	hev_refresh();
}

// Hide all elements
// Triggered by the hide all button
function hev_hide_all()
{
	$('.hev_hidden').removeClass("hev_hidden_unhide");
	$('.hev_visibility').removeClass("hev_visibility_unhide");
	$('.hev_display').removeClass("hev_display_unhide");
	$('.hev_opacity').removeClass("hev_opacity_unhide");
	$('.hev_text-indent').removeClass("hev_text-indent_unhide");
	hev_refresh();
}

// Show specific element
// Triggered by show button
function hev_show(hev_unique_id)
{
	var hev_elementToShow = hev_target.find("[hev_unique_id='" + hev_unique_id + "']");
	hev_elementToShow.is('.hev_hidden').addClass("hev_hidden_unhide");
	hev_elementToShow.is('.hev_visibility').addClass("hev_visibility_unhide");
	hev_elementToShow.is('.hev_display').addClass("hev_display_unhide");
	hev_elementToShow.is('.hev_opacity').addClass("hev_opacity_unhide");
	hev_elementToShow.is('.hev_text-indent').addClass("hev_text-indent_unhide");
}

// Hide specific element
// Triggered by hide button
function hev_hide(hev_unique_id)
{
	var hev_elementToHide = hev_target.find("[hev_unique_id='" + hev_unique_id + "']");
	hev_elementToHide.is('.hev_hidden').removeClass("hev_hidden_unhide");
	hev_elementToHide.is('.hev_visibility').removeClass("hev_visibility_unhide");
	hev_elementToHide.is('.hev_display').removeClass("hev_display_unhide");
	hev_elementToHide.is('.hev_opacity').removeClass("hev_opacity_unhide");
	hev_elementToHide.is('.hev_text-indent').removeClass("hev_text-indent_unhide");
}

// resize sidebarHeight according to window size
// Triggered by $(window).resize
function fix_hev_Sidebar_Height()
{
	$('#hev_sidebar_container').css("height", $(window).outerHeight());
	$('#hev_sidebar_content').css("height", $(window).outerHeight());
}


$(window).resize(function()
{
	fix_hev_Sidebar_Height();
})
